package constructor;

public class Swap {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		int i=10;
		int j=25;
		System.out.println("Before Swaping i="+i+"j="+j);
		i = i+j;//35
		j=i-j;//-15
		i=i-j;//35
		System.out.println("After Swaping: i =" +i+" j="+j);
	
	}
}
  

